const searchBtn=document.querySelector('.booking-button-search');const form=document.querySelector('.form');const formInputs=document.querySelectorAll('.form input');if(form){const checkFormValidity=()=>{let flag=!0;formInputs.forEach((input)=>{if(!input.value){flag=!1}});return flag}
form.classList.remove('form-open');searchBtn.addEventListener('click',(e)=>{e.preventDefault();form.classList.toggle('form-open')});form.addEventListener('submit',(e)=>{if(!checkFormValidity()){e.preventDefault();form.classList.remove('error');form.offsetWidth=form.offsetWidth;form.classList.add('error')}})}
